class LinkedList {
  constructor () {
    this._length = 0;
    this._head = null;
  }

  head () {
    return this._head;
  }

  size () {
    return this._length;
  }

  add (element) {
    const node = new Node(element);

    if (this._head === null) {
      this._head = node;
    } else {
      let current = this._head;

      while (current.next !== null) {
        current = current.next;
      }

      current.next = node; 
    }

    ++this._length;
  }

  remove (element) {
    if (this._head === null) return;

    let previous;
    let current = this._head;

    while (current.next !== null && current.element !== element) {
      previous = current;
      current = current.next;
    }

    if (previous) {
      previous.next = current.next;
    } else {
      this._head = current.next;
    }

    --this._length;
  }
}

